package org.juarez.euler;

public class Problem8 {
	
	private static int product(CharSequence str) {
		int p = 1;
		for (int i = 0; i < str.length(); i++) {
			p *= str.charAt(i) - '0';
		}
		return p;
	}
	
	public static int largestProduct(int n_digits, String str) {
		int max_product = 0;
		
		for (int i = 0; i < str.length() - n_digits + 1; i++) {
			int p = product(str.subSequence(i, i + n_digits));
			max_product = Math.max(max_product, p);			
		}
		
		return max_product;
	}

	public static void main(String[] args) {
		System.out.println(largestProduct(5, ""));
	}

}
